class Solution:
    def maximalRectangle(self, matrix) -> int:
        if not matrix:
            return 0
        m, n = len(matrix), len(matrix[0])

        memory = {} #记忆化
        # 获取 num 数组 在【l，r】 范围内能够延长获得最大的面积 
        def dfs(num, l, r, h):
            if h == 0: # base-case
                return 0
            if memory.get((l, r)):
                return memory[(l, r)]
            hl = num[l-1] if l >0 else 0
            hr = num[r+1] if r<n-1 else 0
            
            left = right = 0
            if  hr: # 向右边延升
                right = dfs(num, l, r+1, min(h, hr))
            if hl: # 向左边延长
                left = dfs(num, l-1, r, min(hl, h))

            # 获取最大的情况
            ans = max(left, right, h*(r-l+1))
            memory[(l, r)] = ans
            return ans

        dp = [0] * n # dp[i] 表示 以 i 为结尾的最多连续 1 的个数

        area = 0
        for i in range(m):
            dp = [dp[j] + 1 if matrix[i][j]=='1' else 0 for j in range(n)]
            memory.clear()
            for i in range(n):
                area = max(dfs(dp, i, i, dp[i]), area)
        
        return area


