import sys

sys.setrecursionlimit(10000000) 


class Solution:
    def numberOfCombinations(self, num: str) -> int:
        from functools import lru_cache
        base = 10**9 + 7
        n = len(num)
        
        @lru_cache(None)
        def dfs(s, last):
            print(n, s, last)
            if s > n: return 0
            elif s == n:
                return 1
            elif num[s] == '0': return 0
            
            length = s - last
            ans = 0
            
            if last < 0 or  num[last:s] <= num[s:s+length]:
                ans += dfs(s+length, s)
            for i in range(length+1, n-s+1):
                if s+i < n and num[s+i] == '0': continue
                ans += dfs(s+i, s)
            
            return ans % base
      
        return dfs(0, -1)



print(
Solution().numberOfCombinations(
""
)
)