class Solution:
    def largestMagicSquare(self, grid) -> int:
        m, n = len(grid), len(grid[0])
        rows = [[0]*(n+1) for i in range(m)]
        cols = [[0]*(m+1) for i in range(n)]

        for i in range(m):
            for j in range(n):
                rows[i][j+1] = rows[i][j] + grid[i][j]
                cols[j][i+1] = cols[j][i] + grid[i][j]
        
        print(rows, cols)
        ans  = 1
        for k in range(min(m, n), 1, -1):
            for i in range(m-k+1):
                for j in range(n-k+1):
                    right_bound = [k+i, j+k]
                    tmp = rows[i][k+j] - rows[i][j]
                    flag = 0
                    for r0 in range(k):
                        if rows[i+r0][j+k] - rows[i+r0][j] != tmp:
                            flag = 1
                            break
                        elif cols[j+r0][k+i]- cols[j+r0][i]!= tmp:
                            flag = 1
                            break

                    if  flag: continue

                    if  sum(grid[i+ro][j+ro] for ro in range(k)) != tmp:
                        continue
                    
                    if sum(grid[i+ro][j+k-1-ro] for ro in range(k)) != tmp:
                        continue
                    return k
        return ans

